import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";

const View = () => {
  const [data, setData] = useState({});
  const getDataFun = async () => {
    try {
      axios
        .get("/getData")
        .then((res) => {
          setData(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDataFun();
  }, []);


  const [upData, setUpData] = useState({})
  openModal


  return (
    <>
      <div className="">
        <h3 className="text-center mb-3"> Testimonials View </h3>
        <table className="table table-bordered">
          <thead>
            <th style={{ width: "80px" }}> S. No </th>
            <th style={{ width: "100px" }}> Profile </th>
            <th style={{ width: "120px" }}> Name </th>
            <th style={{ width: "120px" }}> Post </th>
            <th> Disc </th>
            <th style={{ width: "120px" }}> Created Date </th>
            <th style={{ width: "120px" }}> Updated Date </th>
            <th style={{ width: "70px" }}> Status </th>
            <th style={{ width: "60px" }}> Edit </th>
          </thead>
          <tbody>
            {data.length > 0 && (
              <>
                {data.map((dbData, index) => (
                  <tr key={dbData.id}>
                    <td> {index + 1} </td>
                    <td>
                      {/* {dbData.img} */}
                      <img
                        src={"upload/" + dbData.img}
                        alt=""
                        style={{ width: "100px", borderRadius: "50%" }}
                      />
                    </td>
                    <td> {dbData.name} </td>
                    <td> {dbData.post} </td>
                    <td> {dbData.disc} </td>
                    <td> {dbData.cDate} </td>
                    <td> {dbData.upDate} </td>
                    <td> {dbData.status} </td>
                    <td data-bs-toggle="modal" data-bs-target="#staticBackdrop" onClick={openModal}>
                      <a
                        href
                        data-img={dbData.img}
                        data-name={dbData.name}
                        data-post={dbData.post}
                        data-disc={dbData.disc}
                        data-cDate={dbData.cDate}
                        data-upDate={dbData.upDate}
                        data-status={dbData.status}
                      >
                        <i class="lni lni-pencil-alt"></i>
                      </a>
                    </td>
                  </tr>
                ))}
              </>
            )}
          </tbody>
        </table>
      </div>

      {/* modal for update */}
      <div
        class="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <form action="" method="post">
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="staticBackdropLabel">
                  Modal title
                </h5>
                <button
                  type="button"
                  class="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div class="modal-body px-5 text-center">
                <img
                  src={"upload/avatar3.jpg"}
                  alt=""
                  className="mb-3"
                  style={{ width: "100px", borderRadius: "50%" }}
                />
                <input type="text" name="name" className="form-control mb-3" />
                <input type="text" name="post" className="form-control mb-3" />
                <input type="text" name="disc" className="form-control mb-3" />

                <div className="d-flex">
                  <select name="status" id="" className="form-control me-1">
                    <option value="1"> Active </option>
                    <option value="0"> Inactive </option>
                  </select>
                  <input type="file" name="img" className="form-control ms-1" />
                </div>
              </div>

              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-bs-dismiss="modal"
                >
                  Close
                </button>
                <button type="submit" class="btn btn-primary">
                  Update
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </>
  );
};

export default View;
