import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Update = () => {
  const { id } = useParams("");
  const getDataFun = async () => {
    try {
      axios
        .get("/update/"+id)
        .then((res) => {
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDataFun();
  }, []);

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-6 m-auto border p-3 text-center"  style={{borderRadius:"10px"}}>
            <h1> Update Testimonials </h1>
            <img
              // src={"http://localhost:3000/upload/"+data.img}
              alt=""
              style={{ width: "100px", borderRadius: "50%" }}
              className="mb-4"
            />
            <input type="text" placeholder="name"  onChange={addValue} className="form-control mb-4" />
            <input type="text" placeholder="post"  className="form-control mb-4" />
            <input type="text" placeholder="disc"  className="form-control mb-4" />
            <select name="status" id="" className="form-control mb-4">
              <option value="1"> Active </option>
              <option value="0"> Inactive </option>
            </select>
            <button className="btn btn-primary"> Update </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Update;
