import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Update = () => {
  const { id } = useParams("");
  const [data, setData] = useState({});

  const getDataFun = async () => {
    try {
      axios
        .get("/update/" + id)
        .then((res) => {
          setData(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDataFun();
  }, []);

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-6 m-auto border text-center">
            <h1> Update Testimonials </h1>
            <img
              src={"upload/" +data.img}
              alt=""
              style={{ width: "100px", borderRadius: "50%" }}
            />
            <input type="Name" placeholder="Name" value={data.name} className="form-control" />
          </div>
        </div>
      </div>
    </>
  );
};

export default Update;
