import React from "react";
import axios from "axios";
import { useState } from "react";

const Test = () => {
  const [data, setdata] = useState({
    name: "",
    post: "",
    disc: "",
    img: "",
  });

  let name, value;
  const handleInputData = (e) => {
    name = e.target.name;
    value = e.target.value;
    setdata({ ...data, [name]: value });
  };

  const handleImg = (e) => {
    setNewItem({...newItem, itemImg: event.target.files[0]});
  }

  const sendFormData = async (e) => {
    e.preventDefault();
    const { name, post, disc, img } = data;

    axios
      .post("/test", {
        name: name,
        post: post,
        disc: disc,
        img: img,
      })
      .then((res) => {
        console.log(res.data);
        setdata({ name: "", post: "", disc: "" });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <form action="" method="post">
        <input
          type="text"
          onChange={handleInputData}
          value={data.name}
          name={"name"}
          placeholder="Enter Name"
        />
        <input
          type="text"
          onChange={handleInputData}
          value={data.post}
          name={"post"}
          placeholder="Enter Post"
        />
        <input
          type="text"
          onChange={handleInputData}
          value={data.disc}
          name={"disc"}
          placeholder="Enter Discription"
        />

        <input type="file" />
        <button type="submit" onClick={sendFormData}>
          Submit
        </button>
      </form>
    </>
  );
};

export default Test;
