import React from "react";
import axios from "axios";
import { useState } from "react";

const Test = () => {
  const [data, setdata] = useState({
    name: "",
    post: "",
    disc: "",
  });

  let name, value;
  const handleInputData = (e) => {
    name = e.target.name;
    value = e.target.value;
    setdata({ ...data, [name]: value });
  };

  const sendFormData = async (e) => {

    e.preventDefault();
    const { name, post, disc } = data;

    axios.post('/test',{
        name:name,
        post:post,
        disc:disc
    }).then((res)=>{
        console.log(res.data);
        // const resetValue = '';
        setdata({ name: "",
        post: "",
        disc: ""});

    }).catch((err)=>{
        console.log(err)
    })
  };

  return (
    <>
    <form action="" method="post">
      <input type="text" onChange={handleInputData} value={data.name} name={"name"} placeholder="Enter Name" />
      <input type="text" onChange={handleInputData} value={data.post} name={"post"} placeholder="Enter Post"/>
      <input type="text" onChange={handleInputData} value={data.disc} name={"disc"} placeholder="Enter Discription"/>
      <button type="submit" onClick={sendFormData}> Submit </button>
    </form>
    </>
  );
};

export default Test;
