import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Update = () => {
  const { id } = useParams("");
  // const [data, setData] = useState({});

  const [data, setData] = useState({
    name: "",
    post: "",
    disc: "",
    status: "",
  });

  const getDataFun = async () => {
    try {
      axios
        .get("/update/" + id)
        .then((res) => {
          // setData(res.data);
          // console.log(res.data);
              setData({ ...data, name : res.data.name});
              setData({ ...data, post : res.data.post });
              setData({ ...data, disc : res.data.disc});
              setData({ ...data, status :res.data.status });

        })
        .catch((err) => {
          console.log(err);
        });
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDataFun();
  }, []);

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-6 m-auto border p-3 text-center"  style={{borderRadius:"10px"}}>
            <h1> Update Testimonials </h1>
            <img
              src={"http://localhost:3000/upload/"+data.img}
              alt=""
              style={{ width: "100px", borderRadius: "50%" }}
              className="mb-4"
            />
            <input type="text" placeholder="name" value={data.name} className="form-control mb-4" />
            <input type="text" placeholder="post" value={data.post} className="form-control mb-4" />
            <input type="text" placeholder="disc" value={data.disc} className="form-control mb-4" />
            <select name="status" id="" value={data.status} className="form-control mb-4">
              <option value="1"> Active </option>
              <option value="0"> Inactive </option>
            </select>
            <button className="btn btn-primary"> Update </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Update;
