import React from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";

const View = () => {

  const [data, setData] = useState({});
  const getDataFun = async () => {
    try {
      axios
        .get("/getData")
        .then((res) => {
          setData(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
