  return (
    <>
      <div className="px-3">
        <h3 className="text-center mb-3 mt-2"> Testimonials View </h3>
        <div className="table-responsive" style={{borderRadius:"15px",overflow:"hidden"}}>
        <table className="table table-bordered table-dark table-striped">
          <thead>
            <tr style={{height:"50px"}}>
              <th style={{ width: "100px" }}> Profile </th>
              <th style={{ width: "120px" }}> Name </th>
              <th style={{ width: "120px" }}> Post </th>
              <th> Disc </th>
              <th style={{ width: "120px" }}> Created Date </th>
              <th style={{ width: "150px" }}> Updated Date </th>
              <th style={{ width: "70px" }}> Status </th>
              <th style={{ width: "60px" }}> Edit </th>
            </tr>
          </thead>
          <tbody>
            {data.length > 0 && (
              <>
                {data.map((dbData) => (
                  <tr key={dbData.id}>
                    <td>
                      <img
                        src={"upload/" + dbData.img}
                        alt=""
                        style={{ width: "100px", borderRadius: "50%" }}
                      />
                    </td>
                    <td> {dbData.name} </td>
                    <td> {dbData.post} </td>
                    <td> {dbData.disc} </td>
                    <td> {dbData.cDate} </td>
                    <td> {dbData.upDate} </td>
                    <td> {dbData.status} </td>
                    <td>
                      <Link to={`/update/${dbData._id}`}>  
