import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";

const View = () => {
  const [data, setData] = useState({});
  const getDataFun = async () => {
    try {
      axios
        .get("/getData")
        .then((res) => {
          setData(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDataFun();
  }, []);

  ///

  const [upData, setUpData] = useState({
    newName: "",
    newPost: "",
    newDisc: "",
    dataId: "",
    newStatus: "",
  });

  let name, value;

  const handleUpdateData = (e) => {
    name = e.target.name;
    value = e.target.value;
    setUpData({ ...upData, [name]: value });
  };

  //   const handleImgInp = (e) => {
  //     setUpData({ ...upData, newImg: e.target.files[0] });
  //   };

  const updateTestimonials = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("newName", upData.newName);
    formData.append("newPost", upData.newPost);
    formData.append("newDisc", upData.newDisc);
    formData.append("newStatus", upData.newStatus);
    formData.append("dataId", upData.dataId);
    // formData.append("newImg", upData.newImg);

    if (!upData.newName || !upData.newPost || !upData.newDisc) {
      return alert("Please Fill all the Fields");
    }

    axios
      .post("/update", formData)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const [img, setImg] = useState();
  const fillModal = (e) => {
    setImg(e.target.getAttribute("data-img"));
    setUpData({ ...upData, dataId: e.target.getAttribute("data-id") });
    setUpData({ ...upData, newName: e.target.getAttribute("data-name")});
    setUpData({ ...upData, newPost: e.target.getAttribute("data-post")});
    // setUpData({ ...upData, newDisc: e.target.getAttribute("data-disc")});
    // setUpData({ ...upData, newStatus: e.target.getAttribute("data-status")});
  };

  return (
    <>
      <div className="">
        <h3 className="text-center mb-3"> Testimonials View </h3>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th style={{ width: "80px" }}> S. No </th>
              <th style={{ width: "100px" }}> Profile </th>
              <th style={{ width: "120px" }}> Name </th>
              <th style={{ width: "120px" }}> Post </th>
              <th> Disc </th>
              <th style={{ width: "120px" }}> Created Date </th>
              <th style={{ width: "120px" }}> Updated Date </th>
              <th style={{ width: "70px" }}> Status </th>
              <th style={{ width: "60px" }}> Edit </th>
            </tr>
          </thead>
          <tbody>
            {data.length > 0 && (
              <>
                {data.map((dbData, index) => (
                  <tr key={dbData.id}>
                    <td> {index + 1} </td>
                    <td>
                      {/* {dbData.img} */}
                      <img
                        src={"upload/" + dbData.img}
                        alt=""
                        style={{ width: "100px", borderRadius: "50%" }}
                      />
                    </td>
                    <td> {dbData.name} </td>
                    <td> {dbData.post} </td>
                    <td> {dbData.disc} </td>
                    <td> {dbData.cDate} </td>
                    <td> {dbData.upDate} </td>
                    <td> {dbData.status} </td>
                    <td>
                      <i
                        class="lni lni-pencil-alt"
                        data-id={dbData._id}
                        data-img={dbData.img}
                        data-name={dbData.name}
                        data-post={dbData.post}
                        data-disc={dbData.disc}
                        data-status={dbData.status}
                        data-bs-toggle="modal"
                        data-bs-target="#staticBackdrop"
                        onClick={fillModal}
                      ></i>
                    </td>
                  </tr>
                ))}
              </>
            )}
          </tbody>
        </table>
      </div>

      {/* modal for update */}
      <div
        class="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <form action="" method="post">
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="staticBackdropLabel">
                  Modal title
                </h5>
                <button
                  type="button"
                  class="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div class="modal-body px-5 text-center">
                <img
                  src={"upload/" + img}
                  alt=""
                  className="mb-3"
                  style={{ width: "100px", borderRadius: "50%" }}
                />
                <input
                  type="text"
                  name="newName"
                  className="form-control mb-3"
                  placeholder="Name"
                  onChange={handleUpdateData}
                  value={upData.newName}
                />
                <input
                  type="text"
                  name="newPost"
                  className="form-control mb-3"
                  placeholder="Post"
                  onChange={handleUpdateData}
                  value={upData.newPost}
                />
                <input
                  type="text"
                  name="dataId"
                  className="form-control mb-3 d-none"
                  placeholder="Post"
                  onChange={handleUpdateData}
                  value={upData.dataId}
                />
                <input
                  type="text"
                  name="newDisc"
                  className="form-control mb-3"
                  placeholder="Discription"
                  onChange={handleUpdateData}
                  value={upData.newDisc}
                />

                <div className="d-flex">
                  <select
                    name="newStatus"
                    id=""
                    className="form-control me-1"
                    onChange={handleUpdateData}
                    value={upData.newStatus}
                  >
                    <option value="1"> Active </option>
                    <option value="0"> Inactive </option>
                  </select>
                  {/* <input
                    type="file"
                    name="newImg"
                    className="form-control ms-1"
                    onChange={handleImgInp}
                    accept=".png,.jpg,.jpeg"
                  /> */}
                </div>
              </div>

              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-bs-dismiss="modal"
                >
                  Close
                </button>
                <button
                  type="submit"
                  class="btn btn-primary"
                  onClick={updateTestimonials}
                >
                  Update
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </>
  );
};

export default View;
